-- Seed script to populate initial furniture data
-- Run this after creating the schema and applying the subcategories migration

-- Insert parent categories first (with parent_category_id = NULL)
INSERT INTO categories (name, parent_category_id) VALUES
  ('Dining', NULL),
  ('Seating', NULL),
  ('Storage', NULL),
  ('Bedroom', NULL),
  ('Tables', NULL),
  ('Kitchen Appliances', NULL),
  ('Furniture Packages', NULL)
ON CONFLICT DO NOTHING;

-- Insert subcategories (with parent_category_id references)
-- We'll insert them one by one to properly handle the parent references
DO $$
DECLARE
  dining_id INTEGER;
  seating_id INTEGER;
  storage_id INTEGER;
  bedroom_id INTEGER;
  tables_id INTEGER;
  kitchen_id INTEGER;
  packages_id INTEGER;
BEGIN
  -- Get parent category IDs
  SELECT id INTO dining_id FROM categories WHERE name = 'Dining' AND parent_category_id IS NULL;
  SELECT id INTO seating_id FROM categories WHERE name = 'Seating' AND parent_category_id IS NULL;
  SELECT id INTO storage_id FROM categories WHERE name = 'Storage' AND parent_category_id IS NULL;
  SELECT id INTO bedroom_id FROM categories WHERE name = 'Bedroom' AND parent_category_id IS NULL;
  SELECT id INTO tables_id FROM categories WHERE name = 'Tables' AND parent_category_id IS NULL;
  SELECT id INTO kitchen_id FROM categories WHERE name = 'Kitchen Appliances' AND parent_category_id IS NULL;
  SELECT id INTO packages_id FROM categories WHERE name = 'Furniture Packages' AND parent_category_id IS NULL;

  -- Insert subcategories for Dining
  INSERT INTO categories (name, parent_category_id) VALUES
    ('Dining Tables', dining_id),
    ('Dining Chairs', dining_id),
    ('Dining Sets', dining_id)
  ON CONFLICT DO NOTHING;

  -- Insert subcategories for Seating
  INSERT INTO categories (name, parent_category_id) VALUES
    ('Sofas', seating_id),
    ('Armchairs', seating_id),
    ('Recliners', seating_id)
  ON CONFLICT DO NOTHING;

  -- Insert subcategories for Storage
  INSERT INTO categories (name, parent_category_id) VALUES
    ('Bookshelves', storage_id),
    ('Cabinets', storage_id),
    ('Wardrobes', storage_id)
  ON CONFLICT DO NOTHING;

  -- Insert subcategories for Bedroom
  INSERT INTO categories (name, parent_category_id) VALUES
    ('Beds', bedroom_id),
    ('Bedside Tables', bedroom_id),
    ('Dressers', bedroom_id)
  ON CONFLICT DO NOTHING;

  -- Insert subcategories for Tables
  INSERT INTO categories (name, parent_category_id) VALUES
    ('Coffee Tables', tables_id),
    ('Side Tables', tables_id)
  ON CONFLICT DO NOTHING;

  -- Insert subcategories for Kitchen Appliances
  INSERT INTO categories (name, parent_category_id) VALUES
    ('Refrigerators', kitchen_id),
    ('Ovens', kitchen_id),
    ('Dishwashers', kitchen_id)
  ON CONFLICT DO NOTHING;

  -- Insert subcategories for Furniture Packages
  INSERT INTO categories (name, parent_category_id) VALUES
    ('Starter Packages', packages_id),
    ('Complete Packages', packages_id)
  ON CONFLICT DO NOTHING;
END $$;

